function runTypeEffect(index, term, is_first, cells, timeouts) {
    if (is_first) {
        clearTimeout(timeouts[index]);
        cells[index].innerHTML = '';
        timeouts[index] = setTimeout(runTypeEffect, type_speed_term, index, term, false, cells, timeouts);
        return;
    }
    if (term == null || term === '') {
        return;
    }
    cells[index].innerHTML = cells[index].innerHTML + term.charAt(0);
    timeouts[index] = setTimeout(runTypeEffect, type_speed_term, index, term.substring(1), false, cells, timeouts);
}

