$('.flag-image').click(function(e){
    var img_element = e.currentTarget;
    var accent = img_element.classList.contains('us-flag') ? 'us' : 'uk';
    var key_index = img_element.classList.contains('index0-flag') ? 0 : 1;
    var voice_key = key_list_to_listen[key_index];
    listenFromKey(voice_key, accent);
})

function getAElement(term) {
    var a = document.createElement('a');
    a.className = 'quiz-option-a';
    a.target = '_blank';
    a.href = 'https://tureng.com/search/' + term;
    a.innerText = term;
    return a;
}

function switchLinkAndSpanWithId(span_id){
    var span_element = $("#" + span_id);
    var terms = span_element.text().trim().split(',');
    span_element.text('');
    for (var i = 0; i < terms.length; i++) {
        var t = terms[i].trim();
        var a = getAElement(t);
        span_element.append(a);
        if (i < terms.length - 1) {
            span_element.append(', ');
        }
    }
}

function switchLinkAndSpan(syn_ant){
    if (syn_ant === 'syn' || syn_ant === 'ant' || syn_ant === 'lis1' || syn_ant === 'lis2'){
        switchLinkAndSpanWithId(syn_ant + "-left-quiz-span");
        switchLinkAndSpanWithId(syn_ant + "-right-quiz-span");
        switchLinkAndSpanWithId(syn_ant + "-question-word");
    }else if (syn_ant === 'voc'){
        switchLinkAndSpanWithId("voc-0-quiz-span");
        switchLinkAndSpanWithId("voc-1-quiz-span");
        switchLinkAndSpanWithId("voc-2-quiz-span");
        switchLinkAndSpanWithId("voc-3-quiz-span");
        switchLinkAndSpanWithId("voc-quiz-word");
    }
}

function printQuizProgress(syn_ant) {
    var progress = $("#" + syn_ant + "-progress");
    progress.show();
    var syn_ant_ind = quiz_index_map[syn_ant];
    var easy_hard_ind = quiz_index_map[easy_hard[syn_ant_ind]];
    var num_corrects = number_of_corrects[syn_ant_ind][easy_hard_ind];
    var num_wrongs = number_of_wrongs[syn_ant_ind][easy_hard_ind];
    var num_answered = num_corrects + num_wrongs + 1;
    if (num_answered > 10) {
        num_answered = num_answered % 10;
    }
    var remaining_len = questions[syn_ant_ind][easy_hard_ind].length;
    var over = 10;
    if (remaining_len + num_answered < 10) {
        over = remaining_len + num_answered - 1;
    }
    if (remaining_len === 0) {
        num_answered = over;
    }
    progress.text(num_answered + "/" + over);
}

function selectOption(left_right, correct_wrong, is_selected, syn_ant) {
    var quiz_btn = $("#" + syn_ant + "-" + left_right + "-quiz-btn");
    quiz_btn.attr("class", "synonym-btn-answer synonym-btn-" + correct_wrong);
    if (is_selected) {
        $("#" + syn_ant + "-next-quiz-btn").show();
        $("#" + syn_ant + "-progress").hide();
        $("#" + syn_ant + "-" + left_right + "-quiz-" + correct_wrong).slideDown();
    }
}

function clearOptions(syn_ant) {
    if (syn_ant === 'que' || syn_ant === 'voc'){
        for(var i = 0; i < 4; i++) {
            $("#" + syn_ant + "-" + i + "-quiz-wrong").hide();
            $("#" + syn_ant + "-" + i + "-quiz-correct").hide();
            $("#" + syn_ant + "-" + i + "-quiz-btn").attr("class", "synonym-btn");
        }
    }else {
        $("#" + syn_ant + "-left-quiz-wrong").hide();
        $("#" + syn_ant + "-left-quiz-correct").hide();
        $("#" + syn_ant + "-right-quiz-wrong").hide();
        $("#" + syn_ant + "-right-quiz-correct").hide();
        $("#" + syn_ant + "-left-quiz-btn").attr("class", "synonym-btn");
        $("#" + syn_ant + "-right-quiz-btn").attr("class", "synonym-btn");
    }
    $("#" + syn_ant + "-next-quiz-btn").hide();
    printQuizProgress(syn_ant);
}

