function sliderPlayClicked(e, index){
    if (swiper_guest != null) {

        if (swiper_guest.realIndex === index){
            var slider_iframe = e.getElementsByTagName("iframe")[0];
            slider_iframe.src = "https://www.youtube.com/embed/" + slider_guest_list[index].video + "?autoplay=1&rel=0&modestbranding=1";
            slider_iframe.style.display = '';
            sendSt('GVC', slider_guest_list[index].id);
        }else {
            if (swiper_guest.realIndex - index === -1) {
                swiper_guest.slideNext();
            }else if(swiper_guest.realIndex - index === 1){
                swiper_guest.slidePrev();
            }else if (swiper_guest.realIndex - index < 0) {
                swiper_guest.slidePrev();
            }else {
                swiper_guest.slideNext();
            }
        }

    }
}

