function isEmailValid(email_val) {
    let re = /\S+@\S+\.\S+/;
    return re.test(email_val);
}

var feedback_error_alert_message = "Unable to send the feedback. Check your internet connection and try again, please.";

$("#feedback-send-btn").click(function () {
    let name_input = $("#name-input");
    let email_input = $("#email-input");
    let message_input = $("#message-input");

    var url = "http://localhost:8080/api/v1/feedback/save?";

    let name_val = name_input.val().trim();
    let email_val = email_input.val().trim();
    let message_val = message_input.val().trim();

    var send_flag = true;

    if (name_val === '') {
        if (!name_input.hasClass("alert-input-class")) {
            name_input.addClass("alert-input-class");
        }
        send_flag = false;
    }else {
        if (name_input.hasClass("alert-input-class")) {
            name_input.removeClass("alert-input-class");
        }
    }

    if (!isEmailValid(email_val)) {
        if (!email_input.hasClass("alert-input-class")) {
            email_input.addClass("alert-input-class");
        }
        send_flag = false;
    }else {
        if (email_input.hasClass("alert-input-class")) {
            email_input.removeClass("alert-input-class");
        }
    }

    if (message_val === '') {
        if (!message_input.hasClass("alert-input-class")) {
            message_input.addClass("alert-input-class");
        }
        send_flag = false;
    }else {
        if (message_input.hasClass("alert-input-class")) {
            message_input.removeClass("alert-input-class");
        }
    }

    if (send_flag) {
        url = url + "name=" + encodeURIComponent(name_val);
        url = url + "&email=" + encodeURIComponent(email_val);
        url = url + "&message=" + encodeURIComponent(message_val);

        fetch(url).then(response => {
            if (response.status === 200) {
                $(".feedback-item").slideUp();
                $("#feedback-completed-span").slideDown();
            }else {
                alert(feedback_error_alert_message);
            }
        });
    }
});

function sendQuizSt(syn_ant, easy_hard, is_correct){
    let code_map = {"syn" : "SYS", "ant" : "ANS", "que" : "GRS", "tf" : "TFS", "voc" : "VOS", "lis1" : "L1S", "lis2" : "L2S", "easy" : "EASY", "hard" : "HARD"};

    var dt = code_map[easy_hard] + "-";
    if (is_correct){
        dt += "CORRECT";
    }else {
        dt += "WRONG";
    }
    sendSt(code_map[syn_ant], dt);
}

function sendSt(code, dt){
    var url = "http://localhost:8080/api/v1/st/" + code;
    if (dt != null) {
        url = url + "?detail=" + dt;
    }
    fetch(url).then();
}

